perspectives.style:
@controls {
  top-left {
    search {}

    text {
      value: "Filter by...";
      font-weight: bold;
      font-size: 13;
    }

    focus {
      by: "federal agency";
      as: dropdown;
      placeholder: "Federal Agency";
      multiple: true;
    }

    focus {
      by: "primary topic";
      as: dropdown;
      placeholder: "Primary Topic";
      multiple: true;
    }

    showcase {
      target: element;
      by: "sector";
      as: dropdown;
      placeholder: "Sector";
      multiple: true;
      default: select-none;
      mode: loose;
    }
  }

  top {
    focus {
      by: "element type";
      as: label;
      placeholder: "Focus by";
      multiple: false;
      only: "Federal Agency", "Primary Topic";
    }
  }
}

@settings {
  template: stakeholder;
  cluster: "Primary Topic", "Federal Agency";
  element-scale: scale("Total Program Value", 0.5, 3);
  font-cutoff: 0;
  element-color: categorize("Sector", set3);
}

element["element type"="Federal Agency"] {
  font-size: scale("Size",50, 200);
  text-align: center;
  size: 0;
  font-weight: bold;
}

element["element type"="Primary Topic"] {
  font-size: scale("Size",50, 200);
  text-align: center;
  size: 0;
}
